import { authOptions } from "@/lib/authOptions";
import NextAuth from "next-auth";

declare module "next-auth" {
  interface User {
    id: string;
    username: string;
    name: string;
    email: string;
    role: string;
    token: string;
  }
  interface Session {
    user: {
      id: string;
      username: string;
      name: string;
      email: string;
      role: string;
    };
    token: string;
  }
}

const handler = NextAuth(authOptions);

export { handler as GET, handler as POST };

import { NextRequest, NextResponse } from "next/server";
import jwt from "jsonwebtoken";
import prisma from "@/lib/prisma";

const SECRET_KEY = process.env.JWT_SECRET || "your_secret_key"; // Use a strong secret

export async function POST(req: NextRequest) {
  const { email, name } = await req.json();

  try {
    // Check if the user already exists
    let user = await prisma.user.findUnique({
      where: { email },
    });

    // If the user doesn't exist, create a new one
    if (!user) {
      user = await prisma.user.create({
        data: {
          email,
          name,
          username: email.split("@")[0],
        },
      });
    }

    // Create a JWT token
    const token = jwt.sign(
      {
        id: user.id,
        username: user.username,
        email: user.email,
        role: user.role,
      },
      SECRET_KEY,
      { expiresIn: "10d" } // Token expiration
    );

    // Return the user data and token in the desired format
    return NextResponse.json(
      {
        success: true,
        message: "User successfully authenticated",
        data: { user, token },
      },
      { status: 200 }
    );
  } catch (error) {
    console.error(error);
    return NextResponse.json(
      {
        success: false,
        message: "Error processing request",
      },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import prisma from "@/lib/prisma";
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";

type SignupRequestBody = {
  email: string;
  password: string;
  role?: string;
  name?: string;
};

export async function POST(request: Request) {
  try {
    const body: SignupRequestBody = await request.json();
    const { email, password, role, name } = body;

    if (!email || !password) {
      return NextResponse.json(
        { message: "Email and password are required", success: false },
        { status: 400 }
      );
    }

    // Check if user already exists
    const existingUser = await prisma.user.findUnique({ where: { email } });
    if (existingUser) {
      return NextResponse.json(
        { message: "User with this email already exists", success: false },
        { status: 400 }
      );
    }
    // Hash the password
    const hashedPassword = await bcrypt.hash(password, 10);

    // Create the new user
    const user = await prisma.user.create({
      data: {
        email,
        username: email?.split("@")[0],
        name: name || "Albert",
        password: hashedPassword,
        role: (role || "user") as any,
      },
    });

    // Generate a JWT token
    const token = jwt.sign(
      {
        id: user.id,
        email: user.email,
        username: user.username,
        role: user.role,
      },
      process.env.JWT_SECRET || "secret",
      { expiresIn: "10d" } // Token expires in 10 days
    );

    return NextResponse.json(
      {
        message: "sugnup successful",
        success: true,
        data: { token: token, user: user },
      },
      { status: 201 }
    );
  } catch (error) {
    console.error("Signup error:", error);
    return NextResponse.json(
      { success: false, message: "An error occurred during signup" },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import prisma from "@/lib/prisma"; //

// Update user profile (PUT request)
export async function PUT(
  req: Request,
  { params }: { params: { userId: any } }
) {
  try {
    const body = await req.json();

    const { name } = body;

    if (!name || !params.userId) {
      return NextResponse.json(
        { message: "Missing required fields", success: false },
        { status: 400 }
      );
    }

    // Assume you're using Prisma or another ORM to update the user's profile in the DB
    const updatedUser = await prisma.user.update({
      where: { id: params.userId },
      data: { name },
    });

    return NextResponse.json({
      message: "Profile updated successfully",
      success: true,
      data: updatedUser,
    });
  } catch (error) {
    return NextResponse.json(
      { message: "Error updating profile", success: false },
      { status: 500 }
    );
  }
}
export async function DELETE(
  req: Request,
  { params }: { params: { userId: any } }
) {
  try {
    if (!params.userId) {
      return NextResponse.json(
        { message: "Missing required fields", success: false },
        { status: 400 }
      );
    }

    // Assume you're using Prisma or another ORM to delete the user from the DB
    await prisma.user.delete({
      where: { id: params.userId },
    });

    return NextResponse.json({
      message: "User deleted successfully",
      success: true,
    });
  } catch (error) {
    return NextResponse.json(
      { message: "Error deleting user", success: false },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import prisma from "@/lib/prisma";
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";

type LoginRequestBody = {
  email: string;
  password: string;
};

export async function POST(request: Request) {
  try {
    const body: LoginRequestBody = await request.json();
    const { email, password } = body;

    if (!email || !password) {
      return NextResponse.json(
        { message: "Email and password are required", success: false },
        { status: 400 }
      );
    }

    // Find the user by email
    const user = await prisma.user.findUnique({ where: { email } });

    if (!user) {
      return NextResponse.json(
        { message: "Invalid credentials", success: false },
        { status: 401 }
      );
    }

    if (user.password === null) {
      // means user signed up with google
      return NextResponse.json(
        {
          message: "User signed up with Google. Please use Google login.",
          success: false,
        },
        { status: 401 }
      );
    }

    // Compare the provided password with the stored hash
    const isPasswordValid = await bcrypt.compare(password, user.password);

    if (!isPasswordValid) {
      return NextResponse.json(
        { message: "Invalid credentials", success: false },
        { status: 401 }
      );
    }

    // Generate a JWT token
    const token = jwt.sign(
      {
        id: user.id,
        email: user.email,
        username: user.username,
        role: user.role,
      },
      process.env.JWT_SECRET || "secret",
      { expiresIn: "10d" } // Token expires in 10 days
    );
    return NextResponse.json(
      {
        message: "user logged in successfully",
        success: true,
        data: { token, user: user },
      },
      { status: 200 }
    );
  } catch (error) {
    console.error("Login error:", error);
    return NextResponse.json(
      { message: "An error occurred during login", success: false },
      { status: 500 }
    );
  }
}

